mapper.select:
SELECT * FROM teacher


        AND name LIKE CONCAT('%', #{name}, '%')


        AND job = #{job}


        AND age >= #{minAge}


        AND age <= #{maxAge}


        AND sal >= #{minSal}


        AND sal <= #{maxSal}


        AND addr LIKE CONCAT('%', #{addr}, '%')


ORDER BY id DESC

mapper.delete:
delete from teacher


        AND id = #{id}


        AND id IN

            #{id}




        AND name = #{name}


        AND name LIKE CONCAT('%', #{nameLike}, '%')



        AND addr = #{addr}


        AND addr LIKE CONCAT('%', #{addrLike}, '%')



        AND job = #{job}


        AND job LIKE CONCAT('%', #{jobLike}, '%')



        AND age = #{age}


        AND age >= #{minAge}


        AND age <= #{maxAge}


        AND sal = #{sal}



        AND sal >= #{minSal}



        AND sal <= #{maxSal}


        AND job = #{job} AND age >= #{minAge}



        AND job = #{job} AND age <= #{maxAge}


        AND addr LIKE CONCAT(#{province}, '%')
